			this.curNum.html(this.currentIndex+1);
			this.allNum.html(this.stepsCount);

			// initial refresh
			//if(this.options.autoHeight) {
			//	if(initial) {
			//		this.slidesHolder.css({height: this.slides.eq(this.currentIndex).outerHeight(true) });
			//	} else {
